}


if (!function_exists('imgToBase64')) {
    function imgToBase64($path)
    {
        if (!file_exists($path)) {
            return null;
        }

        $type = pathinfo($path, PATHINFO_EXTENSION);
        $data = file_get_contents($path);

        return 'data:image/png;base64,' . base64_encode($data);
    }
}

if (!function_exists('imgUrlToBase64')) {
    function imgUrlToBase64($path)
    {
        if ($path === null || $path === '') {
            return null;
        }
        $data = file_get_contents($path);
        return 'data:image/svg+xml;base64,' . base64_encode($data);
    }
}

if (!function_exists('base64ToTempImage')) {
    function base64ToTempImage($base64, $name = 'watermark.png')
    {
        if (!$base64) {
            return null;
        }

        $base64 = preg_replace('#^data:image/\w+;base64,#i', '', $base64);
        $path = storage_path('app/' . $name);

        file_put_contents($path, base64_decode($base64));

        return $path;
    }
}
